$(document).ready(function(){class t{constructor(){this.baseUrl=`${window.location.href}.json`}getBookInfo(){return fetch(`${this.baseUrl}`).then(t=>t.json()).then(t=>new o(t))}}class o{constructor(t){this.id=t.id,this.reviews=t.reviews,this.avRating=t.average_rating}}class e{constructor(){this.adapter=new t,this.fetchAndLoadBook()}fetchAndLoadBook(){this.adapter.getBookInfo().then(t=>{t.json})}}new e});