$(document).ready(function(){class t{constructor(){this.baseUrl="https://saras-bookable.herokuapp.com/genres"}getSpecificGenre(){return fetch(`${window.location.href}.json`).then(t=>t.json()).then(t=>new o(t))}}class o{constructor(t){this.id=t.id,this.name=t.name,this.authors=t.authors,this.bookArray=[],t.books.forEach(t=>{this.bookArray.push({title:t.title,author:t.author_name,id:t.id,author_id:t.author_id})})}}class e{constructor(){this.adapter=new t,this.fetchAndLoadGenreInfo(),this.baseUrl="https://saras-bookable.herokuapp.com"}fetchAndLoadGenreInfo(){this.adapter.getSpecificGenre().then(t=>{this.showGenreInfo(t)})}showGenreInfo(t){const o=$("#genre_show_table"),e="<th>Book Title</th> <th>Author</th>",r=t.bookArray.map(t=>this.renderTr(t)).join("");o.html(e),o.append(r)}renderTr(t){return`<tr><td><a href="https://saras-bookable.herokuapp.com/books/${t.id}">${t.title}</a></td><td><a href="https://saras-bookable.herokuapp.com/authors/${t.author_id}">${t.author}</a></td></tr>`}}new e});